ex-direction: column;  align-items: center;  justify-content: center;  padding: 60px 20px;  text-align: center;}.loading i,.error i {  font-size: 48px;  margin-bottom: 16px;  color: #9CA3AF;}.error i {  color: #EF4444;}.loading p,.error p {  margin: 8px 0;  color: #6B7280;  font-size: 14px;}.page-content {  display: flex;  flex-direction: column;  gap: 16px;}.filters-section {  margin-bottom: 16px;}.main-content {  flex: 1;}.btn {  padding: 10px 20px;  border: none;  border-radius: 10px;  font-size: 14px;  font-weight: 600;  cursor: pointer;  transition: all 0.2s ease;  display: inline-flex;  align-items: center;  gap: 8px;}.btn-secondary {  background: #F3F4F6;  color: #6B7280;  border: 1px solid #E5E5E5;}.btn-secondary:hover {  background: #E5E7EB;}.btn-add {  padding: 10px 18px;  border: none;  background: linear-gradient(135deg, #FF8C42, #E67E22);  color: white;  cursor: pointer;  border-radius: 10px;  font-size: 13px;  font-weight: 600;  transition: all 0.2s ease;  display: flex;  align-items: center;  gap: 6px;}.btn-add:hover:not(:disabled) {  background: linear-gradient(135deg, #E67E22, #D35400);}.btn-refresh {  padding: 10px 18px;  border: 2px solid #F0E6D9;  background: white;  color: #666;  cursor: pointer;  border-radius: 10px;  font-size: 13px;  font-weight: 600;  transition: all 0.2s ease;  display: flex;  align-items: center;  gap: 6px;}.btn-refresh:hover:not(:disabled) {  border-color: #FF8C42;  background: #FFF3E0;  color: #FF8C42;}.btn-export {  padding: 10px 18px;  border: 2px solid #10B981;  background: white;  color: #10B981;  cursor: pointer;  border-radius: 10px;  font-size: 13px;  font-weight: 600;  transition: all 0.2s ease;  display: flex;  align-items: center;  gap: 6px;}.btn-export:hover:not(:disabled) {  border-color: #059669;  background: #ECFDF5;  color: #059669;}.btn-add:disabled,.btn-refresh:disabled,.btn-export:disabled {  opacity: 0.6;  cursor: not-allowed;}.btn-action {  width: 32px;  height: 32px;  border: 1px solid #E5E5E5;  background: white;  border-radius: 6px;  cursor: pointer;  display: flex;  align-items: center;  justify-content: center;  color: #666;  transition: all 0.2s ease;  font-size: 12px;}.btn-action:hover:not(:disabled) {  transform: translateY(-1px);  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);}.btn-action:disabled {  opacity: 0.6;  cursor: not-allowed;}.btn-edit {  color: #F59E0B;  border-color: #FEF3C7;  background: #FFFBEB;}.btn-edit:hover:not(:disabled) {  background: #FEF3C7;  border-color: #F59E0B;  color: #D97706;}.btn-delete {  color: #EF4444;  border-color: #FEE2E2;  background: #FEF2F2;}.btn-delete:hover:not(:disabled) {  background: #FEE2E2;  border-color: #EF4444;  color: #DC2626;}.btn-view {  color: #3B82F6;  border-color: #DBEAFE;  background: #EFF6FF;}.btn-view:hover:not(:disabled) {  background: #DBEAFE;  border-color: #3B82F6;  color: #2563EB;}.btn-duplicate {  color: #9333EA;  border-color: #F3E8FF;  background: #FAF5FF;}.btn-duplicate:hover:not(:disabled) {  background: #F3E8FF;  border-color: #9333EA;  color: #7C3AED;}.btn-add-product {  color: #10B981;  border-color: #A7F3D0;  background: #ECFDF5;}.btn-add-product:hover:not(:disabled) {  background: #D1FAE5;  border-color: #10B981;  color: #059669;}@media (max-width: 1400px) {  .stats-grid-main {    grid-template-columns: repeat(3, 1fr);    gap: 12px;  }}@media (max-width: 1200px) {  .stats-grid-main {    grid-template-columns: repeat(2, 1fr);    gap: 12px;  }}@media (max-width: 768px) {  .admin-page-layout {    padding: 12px;  }  .stats-grid-main {    grid-template-columns: 1fr;    gap: 12px;  }  .content-area {    padding: 16px;  }}</style>