<template>  <div class="manager-orders-tab">    <div class="orders-content">      <!-- Include full OrderList functionality from Admin -->      <!-- Manager can only see orders from their branch -->      <OrderList         :is-manager-view="true"         :manager-branch-id="managerBranchId"        :hide-branch-filter="true"      />    </div>  </div></template><script setup>import { defineAsyncComponent, computed } from 'vue';import AuthService from '@/services/AuthService';const OrderList = defineAsyncComponent(() =>   import('@/views/Admin/Order/OrderList.vue'));const managerBranchId = computed(() => {  const user = AuthService.getUser();  return user?.branch_id || null;});</script><style scoped>.manager-orders-tab {  background: transparent;  min-height: calc(100vh - 124px);  padding: 0;}.orders-content {  width: 100%;  height: 100%;}</style>