<template>  <div class="manager-reports-tab">    <div class="reports-content">      <!-- Include full Reports/Dashboard functionality from Admin -->      <!-- Manager only sees reports from their branch -->      <AdminHome         :is-manager-view="true"        :manager-branch-id="managerBranchId"      />    </div>  </div></template><script setup>import { defineAsyncComponent, computed } from 'vue';import AuthService from '@/services/AuthService';const AdminHome = defineAsyncComponent(() =>   import('@/views/Admin/User/Home.vue'));const managerBranchId = computed(() => {  const user = AuthService.getUser();  return user?.branch_id || null;});</script><style scoped>.manager-reports-tab {  background: transparent;  min-height: calc(100vh - 124px);  padding: 0;}.reports-content {  width: 100%;  height: 100%;}</style>