<template>  <div class="manager-reservations-tab">    <div class="reservations-content">      <!-- Include full ReservationList functionality from Admin -->      <!-- Manager can only see reservations from their branch -->      <ReservationList         :is-manager-view="true"        :manager-branch-id="managerBranchId"        :hide-branch-filter="true"      />    </div>  </div></template><script setup>import { defineAsyncComponent, computed } from 'vue';import AuthService from '@/services/AuthService';const ReservationList = defineAsyncComponent(() =>   import('@/views/Admin/Reservation/ReservationList.vue'));const managerBranchId = computed(() => {  const user = AuthService.getUser();  return user?.branch_id || null;});</script><style scoped>.manager-reservations-tab {  background: transparent;  min-height: calc(100vh - 124px);  padding: 0;}.reservations-content {  width: 100%;  height: 100%;}</style>