<template>  <div class="manager-staff-tab">    <div class="staff-content">      <!-- Include full StaffList functionality from Admin -->      <!-- Manager can only see staff from their branch -->      <StaffList         :is-manager-view="true"        :manager-branch-id="managerBranchId"        :hide-branch-filter="true"      />    </div>  </div></template><script setup>import { defineAsyncComponent, computed } from 'vue';import AuthService from '@/services/AuthService';const StaffList = defineAsyncComponent(() =>   import('@/views/Admin/Staff/StaffList.vue'));const managerBranchId = computed(() => {  const user = AuthService.getUser();  return user?.branch_id || null;});</script><style scoped>.manager-staff-tab {  background: transparent;  min-height: calc(100vh - 124px);  padding: 0;}.staff-content {  width: 100%;  height: 100%;}</style>