<template>  <div class="manager-tables-tab">    <div class="tables-content">      <!-- Include full TableList functionality from Admin -->      <!-- Manager can only see tables from their branch -->      <TableList         :is-manager-view="true"        :manager-branch-id="managerBranchId"        :hide-branch-filter="true"      />    </div>  </div></template><script setup>import { defineAsyncComponent, computed } from 'vue';import AuthService from '@/services/AuthService';const TableList = defineAsyncComponent(() =>   import('@/views/Admin/Table/TableList.vue'));const managerBranchId = computed(() => {  const user = AuthService.getUser();  return user?.branch_id || null;});</script><style scoped>.manager-tables-tab {  background: transparent;  min-height: calc(100vh - 124px);  padding: 0;}.tables-content {  width: 100%;  height: 100%;}</style>